		  Node.focusElement = elementName;

		  if (this.dom.tr && this.dom.tr.parentNode) {
		    var dom = this.dom;

		    switch (elementName) {
		      case 'drag':
		        if (dom.drag) {
		          dom.drag.focus();
		        }
		        else {
		          dom.menu.focus();
		        }
		        break;

		      case 'menu':
		        dom.menu.focus();
		        break;

		      case 'expand':
		        if (this._hasChilds()) {
		          dom.expand.focus();
		        }
		        else if (dom.field && this.fieldEditable) {
		          dom.field.focus();
		          util.selectContentEditable(dom.field);
		        }
		        else if (dom.value && !this._hasChilds()) {
		          dom.value.focus();
		          util.selectContentEditable(dom.value);
		        }
		        else {
		          dom.menu.focus();
		        }
		        break;

		      case 'field':
		        if (dom.field && this.fieldEditable) {
		          dom.field.focus();
		          util.selectContentEditable(dom.field);
		        }
		        else if (dom.value && !this._hasChilds()) {
		          dom.value.focus();
		          util.selectContentEditable(dom.value);
		        }
		        else if (this._hasChilds()) {
		          dom.expand.focus();
		        }
		        else {
		          dom.menu.focus();
		        }
		        break;

		      case 'value':
		      default:
		        if (dom.value && !this._hasChilds()) {
		          dom.value.focus();
		          util.selectContentEditable(dom.value);
		        }
		        else if (dom.field && this.fieldEditable) {
		          dom.field.focus();
		          util.selectContentEditable(dom.field);
		        }
		        else if (this._hasChilds()) {
		          dom.expand.focus();
		        }
		        else {
		          dom.menu.focus();
		        }
		        break;
		    }
		  }
